function atLeastOnce (inputString, $container, callback) {
  var input = typeof inputString === 'string' ? inputString.split('') : inputString
  var inputLength = input.length

  var position = [0, 0]
  var positionsVisited = {
    '0,0': true
  }
  var presentsDelivered = 1
  repeat(0)

  function repeat (index) {
    var currentInstruction = input.shift()
    if (currentInstruction) {
      setTimeout(function () {
        changePosition(currentInstruction, position)
        var positionKey = position.toString()
        if (!positionsVisited[positionKey]) {
          positionsVisited[positionKey] = true
          presentsDelivered += 1
          createSquare(position, index, inputLength, $container)
        }
        repeat(++index)
      }, 0)
    } else {
      callback(presentsDelivered)
    }
  }

  function changePosition (instruction, position) {
    switch (instruction) {
      case '^':
        position[1] += 1
        break
      case '>':
        position[0] += 1
        break
      case '<':
        position[0] -= 1
        break
      case 'v':
        position[1] -= 1
        break
      default:
        break
    }
  }
}

function Position (x, y) {
  return {
    setX: function (newX) {
      x = newX
    },
    setY: function (newY) {
      y = newY
    },
    getX: function () {
      return x
    },
    getY: function () {
      return y
    }
  }
}

function Square (position, colour, width, height) {
  this.width = width || 5
  this.height = height || 5
  this.position = position
  this.el = document.createElement('div')
  this.el.className = 'Square'
  this.el.style.position = 'absolute'
  this.el.style.background = colour || 'black'
  this.el.style.width = this.width + 'px'
  this.el.style.height = this.height + 'px'
  this.el.style.left = this.position.getX() + 'px'
  this.el.style.top = this.position.getY() + 'px'
}

function createGrid () {
  var $container = document.createElement('div')
  $container.className = 'container'
  $container.style.width = '100%'
  $container.style.height = '100%'
  $container.style.position = 'absolute'
  document.body.appendChild($container)
  return $container
}

function createSquare (position, index, inputLength, $container) {
  var offsetX = Math.floor($container.offsetWidth / 2)
  var offsetY = Math.floor($container.offsetHeight / 2)
  var colour = shadeColour('#bc999f', (index / inputLength))
  var square = new Square(Position(position[0] * 5 + offsetX, position[1] * 5 + offsetY), colour)
  $container.appendChild(square.el)
}

function presentsDelivered (answer) {
  var $answer = document.createElement('h1')
  $answer.style.textAlign = 'center'
  $answer.appendChild(document.createTextNode(answer))
  document.body.appendChild($answer)
}

function shadeColour (colour, percent) {
  var f=parseInt(colour.slice(1),16),t=percent<0?0:255,p=percent<0?percent*-1:percent,R=f>>16,G=f>>8&0x00FF,B=f&0x0000FF
  return '#'+(0x1000000+(Math.round((t-R)*p)+R)*0x10000+(Math.round((t-G)*p)+G)*0x100+(Math.round((t-B)*p)+B)).toString(16).slice(1)
}

function partOne () {
  atLeastOnce(inputString, createGrid(), presentsDelivered)
}

partOne()
